@import url('https://fonts.googleapis.com/css2?family=Amiri:wght@400;700&family=IBM+Plex+Sans+Arabic:wght@300;400;500;600;700&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

.contract-container {
    font-family: 'IBM Plex Sans Arabic', 'Amiri', serif;
    max-width: 210mm;
    margin: 0 auto;
    background: white;
    color: #2c3e50;
    direction: rtl;
    position: relative;
}

/* Header Styles */
.header {
    background: linear-gradient(135deg, #1e3a3a 0%, #2d5f5f 50%, #1e3a3a 100%);
    color: white;
    padding: 30px 40px;
    position: relative;
    overflow: hidden;
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
}

.header::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grid" width="10" height="10" patternUnits="userSpaceOnUse"><path d="M 10 0 L 0 0 0 10" fill="none" stroke="rgba(255,255,255,0.05)" stroke-width="0.5"/></pattern></defs><rect width="100" height="100" fill="url(%23grid)"/></svg>');
    opacity: 0.3;
}

.header-content {
    display: flex;
    align-items: center;
    gap: 30px;
    position: relative;
    z-index: 2;
}

.logo-container {
    flex-shrink: 0;
}

.company-logo {
    width: 90px;
    height: auto;
    max-height: 90px;
    object-fit: contain;
    background: rgba(255, 255, 255, 0.95);
    padding: 12px;
    border-radius: 15px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
    border: 2px solid rgba(255, 255, 255, 0.3);
}

.company-details {
    flex: 1;
    text-align: right;
}

.company-name {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 10px;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
    letter-spacing: 1px;
}

.document-title {
    font-size: 1.4rem;
    font-weight: 600;
    margin-bottom: 15px;
    color: #e8f4f8;
    border-bottom: 2px solid rgba(255, 255, 255, 0.3);
    padding-bottom: 8px;
    display: inline-block;
}

.contract-info {
    display: flex;
    gap: 30px;
    font-size: 1rem;
    opacity: 0.9;
}

.contract-info span {
    background: rgba(255, 255, 255, 0.1);
    padding: 8px 15px;
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.2);
}

/* Content Styles */
.content {
    padding: 40px;
}

.section {
    margin-bottom: 30px;
}

/* Prevent page breaks within important sections */
.avoid-break {
    page-break-inside: avoid;
    break-inside: avoid;
}

.section-title {
    background: linear-gradient(135deg, #2d5f5f 0%, #1e3a3a 100%);
    color: white;
    padding: 15px 20px;
    border-radius: 8px;
    font-size: 1.1rem;
    font-weight: 700;
    margin-bottom: 20px;
    box-shadow: 0 2px 8px rgba(45, 95, 95, 0.3);
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
}

.info-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
    gap: 15px;
    margin: 20px 0;
}

.info-item {
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
    padding: 15px;
    border-radius: 8px;
    border-right: 4px solid #2d5f5f;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    transition: transform 0.2s ease;
}

.info-label {
    font-weight: 600;
    color: #2d5f5f;
    margin-bottom: 6px;
    font-size: 0.85rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.info-value {
    font-size: 1rem;
    color: #2c3e50;
    font-weight: 500;
    line-height: 1.4;
}

/* Table Styles */
.table-container {
    overflow-x: auto;
    background: white;
    border-radius: 10px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    margin: 20px 0;
    border: 1px solid #e9ecef;
}

table {
    width: 100%;
    border-collapse: collapse;
}

th {
    background: linear-gradient(135deg, #2d5f5f 0%, #1e3a3a 100%);
    color: white;
    padding: 15px;
    text-align: center;
    font-weight: 700;
    font-size: 1rem;
    border: none;
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
}

td {
    padding: 15px;
    text-align: center;
    border-bottom: 1px solid #e9ecef;
    font-weight: 600;
    font-size: 1rem;
    color: #2c3e50;
}

/* Terms List */
.terms-list {
    list-style: none;
    padding: 0;
    counter-reset: term-counter;
}

.terms-list li {
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
    margin: 12px 0;
    padding: 18px;
    border-radius: 8px;
    border-right: 4px solid #2d5f5f;
    font-weight: 500;
    line-height: 1.6;
    position: relative;
    font-size: 0.9rem;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    page-break-inside: avoid;
}

.terms-list li::before {
    content: counter(term-counter);
    counter-increment: term-counter;
    position: absolute;
    right: -15px;
    top: 50%;
    transform: translateY(-50%);
    background: linear-gradient(135deg, #2d5f5f 0%, #1e3a3a 100%);
    color: white;
    width: 30px;
    height: 30px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 700;
    font-size: 0.9rem;
    box-shadow: 0 2px 8px rgba(45, 95, 95, 0.4);
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
}

/* Signatures */
.signatures {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 40px;
    margin-top: 40px;
    padding-top: 30px;
    border-top: 3px solid #e9ecef;
    page-break-inside: avoid;
}

.signature-box {
    text-align: center;
    padding: 25px;
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
    border-radius: 12px;
    border: 2px dashed #2d5f5f;
    min-height: 120px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

.signature-title {
    font-weight: 700;
    color: #2d5f5f;
    margin-bottom: 15px;
    font-size: 1.1rem;
}

.signature-line {
    border-bottom: 2px solid #2d5f5f;
    margin: 20px 0 10px 0;
    height: 40px;
}

.signature-details {
    font-size: 0.85rem;
    color: #6c757d;
    line-height: 1.4;
    font-weight: 500;
}

/* Footer Styles */
.footer {
    background: linear-gradient(135deg, #1e3a3a 0%, #2d5f5f 50%, #34495e 100%);
    color: white;
    padding: 30px 40px;
    margin-top: 50px;
    position: relative;
    overflow: hidden;
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
}

.footer::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="footergrid" width="8" height="8" patternUnits="userSpaceOnUse"><path d="M 8 0 L 0 0 0 8" fill="none" stroke="rgba(255,255,255,0.03)" stroke-width="0.5"/></pattern></defs><rect width="100" height="100" fill="url(%23footergrid)"/></svg>');
}

.footer-content {
    position: relative;
    z-index: 2;
}

.footer-section {
    display: flex;
    align-items: center;
    gap: 25px;
}

.footer-logo {
    flex-shrink: 0;
}

.footer-logo-img {
    width: 60px;
    height: auto;
    max-height: 60px;
    object-fit: contain;
    background: rgba(255, 255, 255, 0.95);
    padding: 8px;
    border-radius: 10px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
}

.footer-info {
    flex: 1;
    text-align: right;
}

.footer-info h3 {
    font-size: 1.3rem;
    font-weight: 700;
    margin-bottom: 15px;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
}

.contact-info {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.contact-item {
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: 0.9rem;
}

.contact-label {
    font-weight: 600;
    color: #e8f4f8;
    min-width: 80px;
}

/* Print-specific styles */
@media print {
    .contract-container {
        box-shadow: none;
        max-width: none;
    }
    
    /* Ensure colors print correctly */
    .header,
    .section-title,
    th,
    .terms-list li::before,
    .footer {
        -webkit-print-color-adjust: exact !important;
        print-color-adjust: exact !important;
    }
    
    /* Footer only on last page */
    .footer {
        display: none;
    }
    
    .contract-container:last-child .footer,
    .footer:last-child {
        display: block !important;
        page-break-before: avoid;
        break-before: avoid;
    }
    
    /* Page break controls */
    .avoid-break {
        page-break-inside: avoid !important;
        break-inside: avoid !important;
    }
    
    .section {
        page-break-inside: auto;
        break-inside: auto;
    }
    
    /* Prevent orphaned content */
    .terms-list li {
        page-break-inside: avoid;
        break-inside: avoid;
    }
    
    .signatures {
        page-break-inside: avoid;
        break-inside: avoid;
    }
    
    /* Ensure proper spacing */
    .content {
        margin-bottom: 0;
    }
}

/* Responsive Design */
@media (max-width: 768px) {
    .content {
        padding: 20px;
    }
    
    .header {
        padding: 20px;
    }
    
    .header-content {
        flex-direction: column;
        text-align: center;
        gap: 20px;
    }
    
    .company-details {
        text-align: center;
    }
    
    .contract-info {
        flex-direction: column;
        gap: 10px;
    }
    
    .info-grid {
        grid-template-columns: 1fr;
    }
    
    .signatures {
        grid-template-columns: 1fr;
        gap: 20px;
    }
    
    .footer-section {
        flex-direction: column;
        text-align: center;
        gap: 15px;
    }
    
    .footer-info {
        text-align: center;
    }
}
